<div class="modal modal-signup">
    <div class="modal-overlay"></div>
    <div class="modal-content">
        <div class="modal-header">
            <span class="close">&times;</span>
        </div>
        <div class="modal-body">
            <h1>Signup in Shader Visual Position</h1>
            <p class="join-msg">Join us & share your caption !</p>
            <div class="error-msg">
                <span class="cross">Ã—</span>
                <p class="message">All fields are required</p>
            </div>
            <form>
                <div class="form-content">
                    <input type="text" name="name" class="signup-input" placeholder="Name">
                </div>
                <div class="form-content">
                    <input type="password" name="password" class="password-input" placeholder="Password">
                </div>
                <div class="form-content">
                    <label>Add picture</label>
                    <input type="file" name="picture" class="file-input" placeholder="Add picture">
                </div>
                <button type="submit" class="submit-btn">Join us</button>
            </form>
        </div>
        <div class="modal-footer">
        </div>
    </div>

</div>